import React, { useEffect, useState } from 'react';
import axios from 'axios';

function Mypage() {
  const [user, setUser] = useState(null);
  const [isEdit, setIsEdit] = useState(false);
  const [updatedUser, setUpdatedUser] = useState({
    email: '',
    username: '',
    password: '',
  });

  useEffect(() => {
    const userId = localStorage.getItem('userId');
    if (userId) {
      axios.get(`http://localhost:8080/api/user/${userId}`)
        .then(response => {
          setUser(response.data);
          setUpdatedUser({
            email: response.data.email,
            username: response.data.username,
            password: '',
          });
        })
        .catch(error => {
          console.error("There was an error fetching user data!", error);
        });
    }
  }, []);

  const handleEditClick = () => {
    setIsEdit(true);
  };

  const handleChange = (e) => {
    setUpdatedUser({
      ...updatedUser,
      [e.target.name]: e.target.value,
    });
  };

  const handleSave = () => {
    const userId = localStorage.getItem('userId');
    axios.post(`http://localhost:8080/api/user/${userId}`, { ...updatedUser, updateAt: new Date() })
      .then(response => {
        setUser(response.data);
        setIsEdit(false);
      })
      .catch(error => {
        console.error("There was an error updating user data!", error);
      });
  };

  const handleCancel = () => {
    setIsEdit(false);
  };

  const handleDelete = () => {
    const userId = localStorage.getItem('userId');
    if (userId) {
      axios.delete(`http://localhost:8080/api/user/${userId}`)
        .then(response => {
          alert("사용자 계정이 삭제되었습니다.");
          // 계정 삭제 후에는 로그인 페이지로 리디렉션
          localStorage.removeItem('userId');
          window.location.href = "/login";
        })
        .catch(error => {
          console.error("There was an error deleting the user data!", error);
          alert("사용자 삭제에 실패했습니다.");
        });
    }
  };

  return (
    <div className="mypage-container">
      {user && (
        <div className="mypage-content">
          <h2>마이페이지</h2>

          {/* 회원 정보 수정 */}
          <div>
            <h3>회원 정보 수정</h3>
            <p>이메일: {isEdit ? <input type="email" name="email" value={updatedUser.email} onChange={handleChange} /> : user.email}</p>
            <p>사용자명: {isEdit ? <input type="text" name="username" value={updatedUser.username} onChange={handleChange} /> : user.username}</p>
            <p>비밀번호: {isEdit ? <input type="password" name="password" value={updatedUser.password} onChange={handleChange} /> : '********'}</p>
            <p>아이디: {user.id}</p>
            <p>권한: {user.role}</p>
            <p>가입일: {user.createAt}</p>
            <p>수정일: {user.updateAt}</p>
            {!isEdit ? (
              <button className="mypage-edit-button" onClick={handleEditClick}>수정</button>
            ) : (
              <div className="mypage-actions">
                <button className="mypage-save-button" onClick={handleSave}>저장</button>
                <button className="mypage-cancel-button" onClick={handleCancel}>취소</button>
              </div>
            )}
          </div>

          {/* 주문 내역 */}
          <div>
            <h3>주문 내역</h3>
            <ul>
              {user.orders && user.orders.length > 0 ? (
                user.orders.map(order => (
                  <li key={order.id}>주문번호: {order.id}, 상태: {order.status}, 주문일: {order.orderDate}</li>
                ))
              ) : (
                <p>주문 내역이 없습니다.</p>
              )}
            </ul>
          </div>

          {/* 장바구니 */}
          <div>
            <h3>장바구니</h3>
            <ul>
              {user.cart && user.cart.length > 0 ? (
                user.cart.map(item => (
                  <li key={item.id}>{item.name} - {item.quantity}개</li>
                ))
              ) : (
                <p>장바구니에 상품이 없습니다.</p>
              )}
            </ul>
          </div>

          {/* 회원 등급 */}
          <div>
            <h3>회원 등급</h3>
            <p>{user.membershipLevel}</p>
          </div>

          {/* 계정 삭제 */}
          <div>
            <button className="mypage-delete-button" onClick={handleDelete}>계정 삭제</button>
          </div>
        </div>
      )}
    </div>
  );
}

export default Mypage;
