import React, { useEffect, useState } from 'react';
import { useParams, Link } from 'react-router-dom';
import axios from 'axios';
import '../cssfile/ProductDetail.css'; // CSS 파일 import

function ProductDetail() {
  const { productId } = useParams();
  const [product, setProduct] = useState(null);
  const [quantity, setQuantity] = useState(1);

  useEffect(() => {
    const fetchProductDetail = async () => {
      try {
        const numericProductId = Number(productId);
        const response = await axios.get(`http://localhost:8080/api/products/${numericProductId}`);
        setProduct(response.data);
      } catch (error) {
        console.error("상품 정보를 가져오는데 실패했습니다:", error);
      }
    };

    fetchProductDetail();
  }, [productId]);

  const formatPrice = (price) => {
    return new Intl.NumberFormat().format(price);
  };

  const handleQuantityChange = (type) => {
    setQuantity((prevQuantity) =>
      type === 'increment' ? prevQuantity + 1 : Math.max(1, prevQuantity - 1)
    );
  };

  return (
    <div className="product-detail">
      {product ? (
        <div className="product-container-horizontal">
          <div className="product-image-container-horizontal">
            <img src={product.imageUrl} alt={product.productnum} className="product-image" />
          </div>
          <div className="product-info-container-horizontal">
            <h2 className="product-title">{product.productnum}</h2>
            <p className="product-price">{formatPrice(product.price)} 원</p>
            <p className="product-date">입고일: {product.arrivaldate}</p>
            <p className="product-content">{product.content}</p>
            <div className="quantity-container">
              <button onClick={() => handleQuantityChange('decrement')} className="quantity-btn">-</button>
              <span className="quantity-value">{quantity}</span>
              <button onClick={() => handleQuantityChange('increment')} className="quantity-btn">+</button>
            </div>
            <button className="add-to-cart-btn">장바구니 담기</button>
          </div>
        </div>
      ) : (
        <p>상품 정보를 불러오는 중입니다...</p>
      )}
      <div className="back-to-home">
        <Link to="/" className="home-button">홈으로 돌아가기</Link>
      </div>
    </div>
  );
}

export default ProductDetail;
